                </Button>
                <Button
                  className="btnLevel"
                  disabled={this.state.level < EXPERT}
                  onClick={() => {
                    this.changeToquiz4();
                    this.articelState();
                    this.checkQuiznLevel();
                    //    this.hideQuizButton();
                  }}
                >
                  Expert
