import React, { Component } from "react";
import { Modal, ModalHeader, Button, ModalBody, ModalFooter } from "reactstrap";

class InstructionsModal extends Component {
  constructor(props) {
    super(props);

    this.state = {
      showModal: false
    };
  }
  handleShow() {
    this.setState({ showModal: true });
  }

  handleClose() {
    this.setState({ showModal: false });
  }

  render() {
    return (
      <Modal isOpen={this.state.showModal}>
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">Guide</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <h4>Centered Modal</h4>
          <p>
            Cras mattis consectetur purus sit amet fermentum. Cras justo odio,
            dapibus ac facilisis in, egestas eget quam. Morbi leo risus, porta
            ac consectetur ac, vestibulum at eros.
          </p>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="danger" onClick={() => this.showModel(true)}>
            open
          </Button>
          <Button variant="danger" onClick={() => this.showModel(false)}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    );
  }
}
export default InstructionsModal;
