import React, { Component } from "react";
import pictures from "../pictures/health_hazard.jpg";
import Questions, { Quizdata } from "./questions";
class Quiz extends Component {
  constructor(props) {
    super(props);
    this.state = {
      options: [],
      currentQuest: 1
    };
  }
  loadQuiz = () => {
    const { currentQuest } = this.state;
    this.setState(() => {
      return {
        questions: Quizdata[currentQuest].question,
        options: Quizdata[currentQuest].options,
        answers: Quizdata[currentQuest].answer
      };
    });
  };
  componentDidMount() {
    this.loadQuiz();
  }

  render() {
    return (
      <div id="lol">
        {this.state.questions}
        <br></br>
        {this.state.options}
        <br></br>
        {this.state.answers}
      </div>
    );
  }
}
export default Quiz;
