import React, { Component } from "react";
import fire from "../../config/fire";
import { Button, ButtonGroup, Toast, ProgressBar } from "react-bootstrap";

class Login extends Component {
  constructor(props) {
    super(props);

    this.state = {
      email: "",
      password: "",
      clicked: "",
      viewPasswordResetModal: false
    };
  }

  login = e => {
    e.preventDefault();

    // this.handleRememberMe(); // Function to toggle persistant login

    fire
      .auth()
      .signInWithEmailAndPassword(this.state.email, this.state.password)
      .then(u => {
        console.log("Log in");
      })
      .catch(error => {
        alert("Please enter a valid email or password");
        console.log(error.message);
      });
  };

  signUp = e => {
    e.preventDefault();

    const validEmail =
      this.state.email.endsWith("@gmail.com") ||
      this.state.email.endsWith("@mumail.ie");

    if (!validEmail) {
      alert("Please enter a valid Email address");
      return;
    }

    fire
      .auth()
      .createUserWithEmailAndPassword(this.state.email, this.state.password)
      .then(u => {})
      .then(u => {
        console.log(u);
      })
      .catch(error => {
        console.log(error);
      });
  };

  render() {
    return (
      <form>
        <input></input>
        <br></br>
        <input></input>
        <Button onClick={this.login}>Login</Button>
        <Button onClick={this.signUp}>Sign Up</Button>
      </form>
    );
  }
}
export default Login;
