import React, { Component } from "react";
import "./home.css";
import {
  Button,
  Nav,
  Navbar,
  Form,
  FormControl,
  Carousel
} from "react-bootstrap";
import fire from "../../config/fire";
import Pictures from "../pictures/farm.jpeg";
import Pictures1 from "../pictures/health_hazard.jpg";
import Pictures2 from "../pictures/saferty_gaurd.jpg";
class Home extends Component {
  logout = e => {
    e.preventDefault();
    fire.auth().signOut();
    console.log("Logged out");
  };
  render() {
    return (
      <div className="background">
        <div>
          <h2 variant="primary" size="lg" block>
            Farm safety guide
          </h2>
          <Carousel>
            <Carousel.Item className="SlidePictures">
              <img
                className="d-block w-100"
                src={Pictures2}
                alt="first slide"
              />
              <Carousel.Caption style={{ textAlign: "center" }}>
                <h3>Did You Know</h3>
                <p>
                  There are approximately 139,860 family farms in Ireland with
                  an average size of 32.7 hectares per holding.
                </p>
              </Carousel.Caption>
            </Carousel.Item>
            <Carousel.Item className="SlidePictures">
              <img className="d-block w-100" src={Pictures} alt="Third slide" />

              <Carousel.Caption>
                <h3>Did You Know</h3>
                <p>
                  Total Irish milk output in 2015 was estimated at 6,982 million
                  litres. From this total milk output in 2015, 507 million
                  litres was consumed as liquid milk. In addition to this
                  200,000 tonnes of butter and 121,800 tonnes of skim milk
                  powder was produced. 215,000 tonnes of cheese was produced in
                  2014. In 2015, total dairy and ingredients exports increased
                  by an estimated 4% to â‚¬3.24 billion.
                </p>
              </Carousel.Caption>
            </Carousel.Item>
            <Carousel.Item className="SlidePictures">
              <img
                className="d-block w-100"
                src={Pictures1}
                alt="Third slide"
              />

              <Carousel.Caption>
                <h3>Did You Know</h3>
                <p>
                  Ireland is the fourth largest sheep meat producer in the EU
                  but is the largest net exporter of sheep meat. With almost
                  83%Praesent commodo cursus magna, vel scelerisque nisl
                  consectetur. of production exported, the sector relies heavily
                  on markets in the UK and the rest of the EU.
                </p>
              </Carousel.Caption>
            </Carousel.Item>
          </Carousel>
        </div>
      </div>
    );
  }
}
export default Home;
