    const { userAns, option, questions, answers, answer } = this.state;
    ///const [setAnswer] = useState(option);

    return (
      <div className="lol">
        <br></br>
        {this.state.questions}
        <br></br>
        {/* {this.state.options.map(
          item => (
            <button
              id="optionsData"
              className="ui floating message options"
              className={userAns === this.state.options ? "selected" : null}
              onClick={() => this.checkAnswer(option)}
            >
              {item}
            </button>
          ) maps the options on the page
        )}
          */}

        {this.state.options.map((lol, options, id, answer) => (
          <Button
          // key={id}
          //  question={questions}
          // option={options}
          // answer={answers}
          // selected={answer => this.checkAns(answer, option)}
          >
            {lol}
