        {answers.map((text, index) => (
          <Button
          // key={index}
          //   onClick={() => {
          //   setAnswer([text]);
          //}}
          >
            {text}
          </Button>
        ))}

        <br></br>
        <Button onClick={() => this.checkAns(option)}>CHECK</Button>
        <Button onClick={this.nextQuestion}>NEXT</Button>
        <br></br>

        {this.state.currentQuest === Quizdata.length - 1
          ? alert("Quiz FINISHED")
          : null}
      </div>
    );
  }
}
export default Quiz;

