import React, { Component } from "react";
import pictures from "../pictures/health_hazard.jpg";
import Questions from "./questions";
class Quiz extends Component {
  state = {
    options: [],
    currentQuest: 0
  };

  loadQuiz = () => {
    this.setState(() => {
      return {
        questions: Questions[currentQuest].questions,
        options: Questions[currentQuest].options,
        answer: Questions[currentQuest].answer
      };
    });
  };
  componentDidMount() {
    this.loadQuiz();
  }

  render() {
    return (
      <div>
        {this.state.questions}
        <img src={pictures} alt="lol" />
      </div>
    );
  }
}
export default Quiz;
