import React, { Component } from "react";
//import "./App.css";
import { BrowserRouter, Switch, Route, Link } from "react-router-dom";
import fire from "./config/fire";
import "bootstrap/dist/css/bootstrap.min.css";
import Home from "./components/home/home";
import Quiz from "./components/quiz/quiz";
import Login from "./components/quiz/login";
import { Button, Col, Nav, Navbar } from "react-bootstrap";
import Signup from "./components/quiz/signup";
import Welcome from "./components/home/welcome";
import { FiLogIn } from "react-icons/fi";
import { TiHome } from "react-icons/ti";
//import login from ".login/components/quiz/login";

class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      viewquiz: false,
      viewHome: false,
      viewLogin: false,
      viewSignup: false,
      user: {}
    };
  }

  componentDidMount() {
    this.authListener();
  }

  authListener() {
    fire.auth().onAuthStateChanged(user => {
      if (user) {
        this.setState({ user });
      } else {
        this.setState({ user: null });
      }
    });
  }

  changetoQuiz = () => {
    this.setState({
      viewquiz: !this.state.viewquiz,
      viewHome: false,
      viewLogin: false,
      viewSignup: false
    });
  };

  changetoLogin = () => {
    this.setState({
      viewLogin: true,
      viewHome: false,
      viewSignup: false
    });
  };

  changetoSignup = () => {
    this.setState({
      viewSignup: true,
      viewHome: false,
      viewLogin: false
    });
  };
  changetoHome = () => {
    this.setState({
      viewHome: true,
      viewLogin: false
    });
  };

  render() {
    return (
      <body>
        <div className="App">
          <BrowserRouter>
            <Navbar bg="primary" variant="dark">
              <Button onClick={this.changetoHome}>
                <Link to="/">
                  Home <TiHome />
                </Link>
              </Button>
              <Nav className="mr-auto">
                <Button onClick={this.changetoLogin}>
                  <Link to="/Login">
                    Login <FiLogIn />
                  </Link>
                </Button>

                <Button onClick={this.changetoSignup}>
                  <Link to="/Signup">Signup</Link>
                </Button>
              </Nav>
            </Navbar>
            {this.state.viewHome ? (
              <Route path="/Home" component={Home} exact />
            ) : null}
            {this.state.viewLogin ? (
              <Route path="/Login" component={Login} exact />
            ) : null}
            {this.state.viewSignup ? (
              <Route path="/Signup" component={Signup} exact />
            ) : null}
          </BrowserRouter>

          {/*  {this.state.viewHome ? <Home /> : null}
          {this.state.viewLogin ? <Login /> : null}
          {this.state.viewSignup ? <Signup /> : null}
        */}

          {/* {this.state.user ? <Welcome /> : <Home />}*/}
          {this.state.viewHome ? <Home /> : null}
          <div>
            <Button onClick={this.changetoQuiz}>Enter The site</Button>
            <Col md={12}>{this.state.viewquiz ? <Quiz /> : null}</Col>
          </div>
        </div>
      </body>
    );
  }
}

export default App;
